class weather():
    def get_location(city):
        try:
            city_code = location[city]
        except Exception as e:
            return False
        return city_code


    def get_weather_detail(site):
        try:
            url = 'http://www.nmc.cn/f/rest/real/' + site + '?_=' + str(int(time.time() * 1000))
            res = requests.get(url)
            body = json.loads(res.text)
            we = body['weather']
            wind = body['wind']
            station = body['station']

            data = '省份: %s\n' % station['province']
            data += '🏙城市: %s\n' % station['city']
            data += '💨风向: %s\n' % wind['direct']
            data += '💨风力: %s\n' % wind['power']
            data += '💨风速: %s 米/秒\n' % wind['speed']
            data += '🌡️温度: %s 摄氏度\n' % we['temperature']
            data += '🤒体感温度: %s 摄氏度\n' % we['feelst']
            data += '🤯气压: %s 百帕\n' % we['airpressure']
            data += '☀️天气: %s\n' % we['info']
            data += '气象站最近一次的更新时间:\n %s' % body['publish_time']
        except Exception as e:
            data = '暂时查无该城市的天气预报'
        return data

